import { StyleSheet } from "react-native";

const styles = StyleSheet.create({
    container: {
        borderTopColor: '#c9cdd0',
        borderTopWidth: 8,
        width: '100%'
    },
    ctnHeader: {
        paddingHorizontal: 16,
        paddingVertical: 20,
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
    },
    imageUser: {
        width: 40,
        height: 40,
        borderRadius: 50,
        borderColor: '#c54e22',
        borderWidth: 0.5
    },
    flexRow: {
        flexDirection: 'row',
        alignItems: 'center',
    }
});

export default styles;
